INSERT INTO text (id, lang, text) VALUES ( 'guide-dashboard-user-personal', 'ja', 'プロジェクトを作成した場合にのみ、このデータを記入すべきである、あなたはそれが協調融資とサイト名を介してサポートされるようにします。私たちはあなたが必要な協調融資を得る場合にご連絡し、対応する預金を行うことができるように、このセクションの情報が必要です。' );
