INSERT INTO text (id, lang, text) VALUES ( 'tooltip-project-support-type-lend', 'ja', '所定の期間中にプロジェクト、またはインフラストラクチャまたはスペースのローンのために必要な材料の一時的な融資。また、つまり、ギフト永久貸付を意味するものではありかもしれません:)' );
